package BasicProgram;

public class num {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 12;
		if (num > 0) {
			System.out.println("positive");
		} else if (num == 0) {
			System.out.println("0");
		} else if (num < 0) {
			System.out.println("negative");
		}

	}

}
